import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/styles.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeModal, setActiveModal] = useState(null); // Hanterar vilken modal som är öppen

  useEffect(() => {
    axios.get('https://api.github.com/users/AlexanderG-CA/repos')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching GitHub projects', error));
  }, []);

  const openModal = (event, projectId) => {
    event.preventDefault(); // Förhindrar att länken triggas
    event.stopPropagation(); // Stoppar klick från att bubbla upp
    setActiveModal(projectId);
  };

  const closeModal = (event) => {
    event.stopPropagation(); // Stoppar klick från att bubbla upp
    setActiveModal(null);
  };

  return (
    <div className="portfolio-page">
      {/* Portfolio Header */}
      <header>
        <h1>Portfolio</h1>
      </header>

      {/* Main Content */}
      <main>
        <section className="portfolio">
          {loading ? (
            <p>Laddar projekt...</p>
          ) : (
            projects.map((project) => (
              <div key={project.id} className="project">
                <a 
                  href={project.html_url} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="project-link"
                >
                  <img src={project.owner.avatar_url} alt={project.name} />
                  <h3>{project.name}</h3>
                </a>
                {/* Knapp som öppnar modalen utan att trigga länken */}
                <button className="info-button" onClick={(event) => openModal(event, project.id)}>Mer info</button>

                {/* Modal placeras inuti project-div men visas ovanför med z-index */}
                {activeModal === project.id && (
                  <div className="modal" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                      <span className="close" onClick={closeModal}>&times;</span>
                      <h2>{project.name}</h2>
                      <p>{project.description || 'Det här är den första iterationen av denna hemsidan innan dynamiska aspekter tillförts.'}</p>
                    </div>
                  </div>
                )}
              </div>
            ))
          )}
        </section>
      </main>

      {/* Footer */}
      <footer>
        <p>&copy; 2025 Mitt CV</p>
      </footer>
    </div>
  );
}

export default Portfolio;
