import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/styles.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeModal, setActiveModal] = useState(null);

  useEffect(() => {
    axios.get('https://api.github.com/users/AlexanderG-CA/repos')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching GitHub projects', error));
  }, []);

  // Funktion för att öppna modal och stoppa event-bubbling
  const openModal = (event, projectId) => {
    event.preventDefault(); 
    event.stopPropagation(); 
    setActiveModal(projectId);
  };

  // Funktion för att stänga modal vid klick utanför modalens innehåll
  const closeModal = (event) => {
    if (event.target.classList.contains("modal-overlay")) {
      setActiveModal(null);
    }
  };

  return (
    <div className="portfolio-page">
      <header>
        <h1>Portfolio</h1>
      </header>

      <main>
        <section className="portfolio">
          {loading ? (
            <p>Laddar projekt...</p>
          ) : (
            projects.map((project) => (
              <div key={project.id} className="project">
                <a 
                  href={project.html_url} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="project-link"
                >
                  <img src={project.owner.avatar_url} alt={project.name} />
                  <h3>{project.name}</h3>
                </a>

                {/* Knapp som öppnar modalen */}
                <button className="info-button" onClick={(event) => openModal(event, project.id)}>
                  Mer info
                </button>

                {/* Modal overlay som endast stänger vid klick på overlayen */}
                {activeModal === project.id && (
                  <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                      <span className="close" onClick={() => setActiveModal(null)}>&times;</span>
                      <h2>{project.name}</h2>
                      <p><strong>Beskrivning:</strong> {project.description || 'Ingen beskrivning tillgänglig.'}</p>
                      <p><strong>Skapat:</strong> {new Date(project.created_at).toLocaleDateString()}</p>
                      <p><strong>Senast uppdaterad:</strong> {new Date(project.updated_at).toLocaleDateString()}</p>
                      <p><strong>Stjärnor:</strong> ⭐ {project.stargazers_count}</p>
                      <p><strong>Språk:</strong> {project.language || 'Okänt'}</p>
                      <a href={project.html_url} target="_blank" rel="noopener noreferrer">
                        <button className="info-button">Besök GitHub-repo</button>
                      </a>
                    </div>
                  </div>
                )}
              </div>
            ))
          )}
        </section>
      </main>

      <footer>
        <p>&copy; 2025 Mitt CV</p>
      </footer>
    </div>
  );
}

export default Portfolio;
