import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/styles.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [hoveredProject, setHoveredProject] = useState(null); // Tracks hovered project

  useEffect(() => {
    axios.get('https://api.github.com/users/AlexanderG-CA/repos')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching GitHub projects', error));
  }, []);

  return (
    <div className="portfolio-page">
      <header>
        <h1>Portfolio</h1>
      </header>

      <main>
        <section className="portfolio">
          {loading ? (
            <p>Laddar projekt...</p>
          ) : (
            projects.map((project) => (
              <div key={project.id} className="project">
                <a 
                  href={project.html_url} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className="project-link"
                >
                  <img src={project.owner.avatar_url} alt={project.name} />
                  <h3>{project.name}</h3>
                </a>
                {/* Hover-triggered modal */}
                <button 
                  className="info-button" 
                  onMouseEnter={() => setHoveredProject(project.id)} 
                  onMouseLeave={() => setHoveredProject(null)}
                >
                  Mer info
                </button>

                {/* Modal appears only when hovering */}
                {hoveredProject === project.id && (
                  <div className="hover-modal">
                    <h2>{project.name}</h2>
                    <p><strong>Beskrivning:</strong> {project.description || 'Det här är den första iterationen av denna hemsidan innan dynamiska aspekter tillförts.'}</p>
                    <p><strong>Skapat:</strong> {project.created_at ? project.created_at.split('T')[0] : 'Okänt'}</p>
                    <p><strong>Senast uppdaterad:</strong> {project.updated_at ? project.updated_at.split('T')[0] : 'Okänt'}</p>
                    <p><strong>Stjärnor:</strong> ⭐ {project.stargazers_count || 0}</p>
                    <p><strong>Språk:</strong> {project.language || 'Ej angivet'}</p>
                    <a href={project.html_url} target="_blank" rel="noopener noreferrer" className="github-link">
                      Besök GitHub-repo
                    </a>
                  </div>
                )}
              </div>
            ))
          )}
        </section>
      </main>

      <footer>
        <p>&copy; 2025 Mitt CV</p>
      </footer>
    </div>
  );
}

export default Portfolio;
