import './assets/styles.css'; // Kontrollera att detta är rätt sökväg
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Importera dina sidor
import Home from './components/Home.jsx';
import About from './components/About.jsx';
import Cv from './components/Cv.jsx';
import Portfolio from './components/Portfolio.jsx';
import Contact from './components/Contact.jsx';
import NotFound from './components/NotFound.jsx';

function App() {
  const [menuActive, setMenuActive] = useState(false);
  const [easterEggActive, setEasterEggActive] = useState(false); // NY STATE

  // Funktion för att toggla hamburgarmenyn
  const toggleMenu = () => {
    setMenuActive(!menuActive);
  };

  // Hantera påskägg - ändra bakgrundsbild
  const handleLogoClick = () => {
    setEasterEggActive(!easterEggActive);
    document.body.style.backgroundImage = easterEggActive
      ? "none"
      : "url('/profile.jpg')";  // Se till att pathen matchar public-mappen
  };

  return (
    <Router>
      {/* Navigation */}
      <nav>
        <div className="nav-container">
          <div className="logo" onClick={handleLogoClick}>Mitt CV</div> {/* Lägger till onClick */}
          <div className="hamburger" onClick={toggleMenu}>
            ☰
          </div>
          <ul className={`nav-links ${menuActive ? 'active' : ''}`}>
            <li><Link to="/" onClick={() => setMenuActive(false)}>Hem</Link></li>
            <li><Link to="/about" onClick={() => setMenuActive(false)}>Om mig</Link></li>
            <li><Link to="/cv" onClick={() => setMenuActive(false)}>CV</Link></li>
            <li><Link to="/portfolio" onClick={() => setMenuActive(false)}>Portfolio</Link></li>
            <li><Link to="/contact" onClick={() => setMenuActive(false)}>Kontakt</Link></li>
          </ul>
        </div>
      </nav>

      {/* Routes */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/cv" element={<Cv />} />
        <Route path="/portfolio" element={<Portfolio />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="*" element={<NotFound />} /> {/* Hanterar 404 */}
      </Routes>
    </Router>
  );
}

export default App;
