import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/styles.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('https://api.github.com/users/AlexanderG-CA/repos')
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching GitHub projects', error));
  }, []);

  function openModal(event) {
    event.preventDefault(); // Förhindrar att sidan laddas om
    document.getElementById("myModal").style.display = "block";
};

  const closeModal = (modalId) => {
    document.getElementById(modalId).style.display = 'none';
  };

  return (
    <div className="portfolio-page">
      {/* Portfolio Header */}
      <header>
        <h1>Portfolio</h1>
      </header>

      {/* Main Content */}
      <main>
        <section className="portfolio">
          {loading ? (
            <p>Laddar projekt...</p>
          ) : (
            projects.map((project) => (
              <a 
                key={project.id} 
                href={project.html_url} 
                target="_blank" 
                rel="noopener noreferrer" 
                className="project-link"
              >
                <div className="project">
                  <img src={project.owner.avatar_url} alt={project.name} />
                  <h3>{project.name}</h3>
                  {/* Knapp som öppnar modalen utan att trigga länken */}
                  <button onClick={(event) => openModal(event, `modal${project.id}`)}>Mer info</button>
                </div>
              </a>
            ))
          )}
        </section>
      </main>

      {/* Modals */}
      {projects.map((project) => (
        <div id={`modal${project.id}`} className="modal" key={project.id}>
          <div className="modal-content">
            <span className="close" onClick={() => closeModal(`modal${project.id}`)}>&times;</span>
            <h2>{project.name}</h2>
            <p>{project.description || 'Det här är den första iterationen av denna hemsidan innan dynamiska aspekter tillförts.'}</p>
          </div>
        </div>
      ))}

      {/* Footer */}
      <footer>
        <p>&copy; 2025 Mitt CV</p>
      </footer>
    </div>
  );
}

export default Portfolio;
