// Funktion för att hantera hamburgarmenyn
function toggleMenu() {
  const navLinks = document.getElementById('navLinks');
  navLinks.classList.toggle('active');
}

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../assets/styles.css';

function Portfolio() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('https://api.github.com/users/AlexanderG-CA/repos')  
      .then(response => {
        setProjects(response.data);
        setLoading(false);
      })
      .catch(error => console.error('Error fetching GitHub projects', error));
  }, []);

  const openModal = (modalId) => {
    document.getElementById(modalId).style.display = 'block';
  };

  const closeModal = (modalId) => {
    document.getElementById(modalId).style.display = 'none';
  };

  return (
    <div className="portfolio-page">

      {/* Portfolio Header */}
      <header>
        <h1>Portfolio</h1>
      </header>

      {/* Main Content */}
      <main>
        <section className="portfolio">
          {loading ? (
            <p>Laddar projekt...</p>
          ) : (
            projects.map((project) => (
              <div key={project.id} className="project">
                <img src={project.owner.avatar_url} alt={project.name} />
                <h3>{project.name}</h3>
                <button onClick={() => openModal(`modal${project.id}`)}>Mer info</button>
              </div>
            ))
          )}
        </section>
      </main>

      {/* Modals */}
      {projects.map((project) => (
        <div id={`modal${project.id}`} className="modal" key={project.id}>
          <div className="modal-content">
            <span className="close" onClick={() => closeModal(`modal${project.id}`)}>&times;</span>
            <h2>{project.name}</h2>
            <p>{project.description || 'Inget projektbeskrivning tillgänglig.'}</p>
          </div>
        </div>
      ))}

      {/* Footer */}
      <footer>
        <p>&copy; 2025 Mitt CV</p>
      </footer>
    </div>
  );
}



export default Portfolio;
